import {creaeteSlice} from '@reduxjs/toolkit';

export const topicsSlice = creaeteSlice({
    name: 'topics',
    initialState: {
        topics:{}
    },
    reducers:{
        addTopic: (state, action)=>{
            const {id, name, icon} = action.playload;
            state.topics[id]={
                id: id,
                name: name,
                icon: icon,
                quizId: []
            };
        },
        addQuizId: (state, action)=>{
            //Access topic to add quiz
            const {topicId, id} = action.playload;
            state.topics[topicId].quizId.push(id);
        }
    }
});

export const {addTopic, addQuizId} = topicsSlice.actions;
export const topicSelector = (state) => state.topics.topics;
export default topicsSlice.reducers;
