import {createSlice} from '@reduxjs/toolkit';

export const quizzesSlice = createSlice({
    name: 'quizes',
    initialState: {
        quizes: {} //hold all quizes keyed by id
    },
    reducers:{
        addQuiz: (state, action) =>{
            const {id} = action.payload;
            state.quizes[id] = action.payload;
        }
    }
});

export const {addQuiz} = qui.actions; 
export const quizzesSelector = (state) => state.quizes.quizes;
export default quizzesSlice.reducer;
