import {createSlice} from '@reduxjs/toolkit';

export const quizzesSlice = createSlice({
    name: 'quizes',
    initialState: {
        quizes: {} //hold all quizes keyed by id
    },
    reducers:{
        addQuizzes: (state, action) =>{
            const {name, topicId, cardId, id} = action.payload;
            state.quizzes[quizId] = {
                id: quizId,
                name: name,
                topicId: topicId,
                cardIds: cardId
            };
        }
    }
});

export const {addQuizzes} = quizzesSlice.actions; 
export const quizzesSelector = (state) => state.quizes.quizes;
export default quizzesSlice.reducer;
